mapper.select:
select items_id,i_name,i_price,i_photos,number
from car,items
where i_id=items_id and i_status=0 and user_id=#{uId}

mapper.update:
update car
set number = number+1
where items_id = #{iId} and user_id=#{uId}

update car
set number = number-1
where items_id = #{iId} and user_id=#{uId}

mapper.select:
select seller_id
from items,store
where store_id=st_id and i_id=#{iId}

select count(*) from(
select *
from car
where items_id=#{iId} and user_id=#{uId}
)as total
